import React, {useState, useMemo, useContext} from 'react';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import {Helmet} from 'react-helmet';
import {Home, Details} from './pages/Home';
import {AddRecipie} from './pages/AddRecipie';
import {Login} from './pages/Login';
import Footer from './components/Footer';
import NavBar from './components/NavBar';
import './App.css';
import './style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserContext from './UserContext';
// import PrivateRoute from './PrivateRoute';

function App() {

  const [userInfo, setUserInfo] = useState(null);
  const value = useMemo(() => ({ userInfo, setUserInfo }), [userInfo, setUserInfo]);

  // const {userInfo, setUserInfo} = useContext(UserContext);

  return (
    <BrowserRouter>
    <div className='App'>
    <Helmet>
    <title>Home</title>
    </Helmet>
    <NavBar />
    <Routes>
      <UserContext value={userInfo, setUserInfo}></UserContext>
      <Route path="/" element={<Home />}></Route>
      <Route path="/login" element={<Login />}></Route>
      <Route path="/details/:id" element={<Details />}></Route>
      {/* <Route path='/recipie/add' element={<PrivateRoute />} > */}
      <Route path="/recipie/add" element={<AddRecipie />}></Route>
      {/* </Route> */}
    </Routes>
    <Footer/>
    </div>
    </BrowserRouter>
  );
}

export default App;
