import React, {useState, useEffect} from 'react';
import {Link, useParams} from 'react-router-dom';
import axios from 'axios';
import {Container, Row, Card, Form, Button, Col} from 'react-bootstrap';

const AddRecipie = () => {
    const initialFormState = {}
    const [recipies, setData] = useState([]);

    useEffect(() => {
        getRecipies();
    }, []);

    const getRecipies = async () => {
        const response = await axios.get("http://localhost:3000/api/recipies", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        });
        if (response.status === 200){
            setData(response.data);
        }

        console.log(recipies)
    }

    return(
        <Container>
        <Row>
            <Col xs={{span: 8, offset: 2}}>
            <Card>
            <Card.Header>Dodaj przepis</Card.Header>
            <Card.Body>
            <Form>
            <Form.Group className="mb-2">
                <Form.Label>Nazwa potrawy</Form.Label>
                <Form.Control type="text" placeholder="Nazwa potrawy" name="dish-name" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            <Button variant="primary" type="submit">
                Submit
            </Button>
            </Form>
            </Card.Body>
            </Card>
            </Col>
        </Row>
    </Container>
    )
}

export {AddRecipie};
