import React, {useState, useEffect, useContext} from 'react';
import {Link, useParams} from 'react-router-dom';
import axios from 'axios';
import {Container, Row, Card, Badge, Button} from 'react-bootstrap';
import UserContext from '../UserContext';


const Details = () =>{

    const dish_id = useParams().id;

    const {userInfo, setUserInfo} = useContext(UserContext);

    const [recipie, setData] = useState([]);

    useEffect(() => {
        getRecipie();
    }, []);

    const getRecipie = async () => {
        const response = await axios.get("http://localhost:3000/api/recipies/details/" + dish_id, {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        });
        if (response.status === 200){
            setData(response.data);
        }
    }
    

    return(
        <Container>
            <Card style={{ width: '100%' }}>
            <center>
            <Card.Img variant="top" src={`/img/${recipie.photo}`} className={`dish-photo`}/>
            </center>
            <Card.Body>
                <Card.Title><h1>{recipie.name}</h1></Card.Title>
                <Card.Text>
                {recipie.description}
                </Card.Text>
                <Card.Text>
                <h4>Czas wykonania</h4>
                {recipie.time} min.
                </Card.Text>
                <Card.Text>
                <h4>Składniki</h4>
                {recipie.ingredients}
                </Card.Text>
                <Card.Text>
                <h4>Wykonanie</h4>
                {recipie.execution}
                </Card.Text>
                <br />
                <div style={{float: 'left'}}>
                <b>Autor: </b> {recipie.login}
                </div><br />
                <Link className={`btn btn-primary btn`} to={`/recipie/edit/${useParams().id}`}>Edytuj przepis</Link>
                <Link className={`btn btn-danger btn`} to={`/recipie/edit/${useParams().id}`}>Usuń przepis</Link>
            </Card.Body>
            </Card>
        </Container>
    );

}

export {Details};
